document.addEventListener("DOMContentLoaded",function(){const n=document.querySelectorAll("[data-src]"),e=new IntersectionObserver(t=>{t.forEach(t=>{t.isIntersecting&&(t.target.src=t.target.dataset.src,t.target.removeAttribute("data-src"),e.unobserve(t.target))})});n.forEach(t=>e.observe(t));let t=!1;document.addEventListener("mouseleave",function(e){e.clientY<0&&!t&&(t=!0,document.querySelector(".exit-popup")?.classList.add("show"))})})